import React, { useEffect, useState } from 'react';
import { View, ActivityIndicator } from 'react-native';
import Heading from "../components/Heading";
import SearchButton from '../components/SearchButton';
import TextInputBox from "../components/TextInput"
import { fetchGeoData } from '../fetchData';
//import styles from "../styles/ButtonsStyle"
import styles from "../styles/ViewStyle"

const SearchCountryScreen = ({navigation}) => {
  const [isLoading, setIsLoading] = useState(false)
  const [inputText, setText] = useState('')
  const [population, setPopulation] = useState()
  const [name, setName] = useState('')

  const handleChange = (e) =>{
    setText(e.target.value)
  }
  const handleResponse = (r) => {
    let cities = []
    for(let i = 0; i < r['geonames'].length; i++){
      if(r['geonames'][i]['fclName'].includes("city")){
        citties.push(r['geonames'][i])
      }
    }

    console.log(cities)
    setPopulation(r['geonames'][0]['population'])
    setName(r['geonames'][0]['name'])
  }
  const handleClick = () =>{
    setIsLoading(true)
    fetchGeoData(inputText).then(response => {handleResponse(response)}).catch(console.log("Error"))
    setIsLoading(false)
  }

  useEffect(() => {
    if(population > 0 && name != ''){
      navigation.navigate("CountryResults", {
        population: population,
        name: name
      })
    }
  }, [{population, name}])
    return (
        <View style={styles.viewContainer}>
          <Heading   text="SEARCH BY COUNTRY" />
          <TextInputBox plcHolder="Enter a country..."/>
          {!isLoading ? (<SearchButton onPress={handleClick} />) : <ActivityIndicator size="large" />}
       </View>
    )
  }
  export default SearchCountryScreen;
